/* list.c */
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
typedef struct ListElement {
	struct ListElement* next;
	void* data;
}ListElement;

typedef struct List{
	ListElement* head;
	size_t t_size;
}List;

void l_init(List* list, size_t t_size){
	list-&gt;head=NULL;
	list-&gt;t_size=t_size;
}

void l_append(List* list, void* data) {
	void* new_data = malloc(list-&gt;t_size);
	memcpy(new_data,data,list-&gt;t_size);
	ListElement* e = malloc(sizeof(struct ListElement));
	e-&gt;next = NULL;
	e-&gt;data=new_data;
	if(list-&gt;head == NULL){
		list-&gt;head=e;
		return;
	}
	ListElement* tmp = list-&gt;head;
	while (tmp-&gt;next != NULL) {
		tmp = tmp-&gt;next;	
	} 
	tmp-&gt;next=e;
}

void l_insert(List* list, void* data, unsigned position){
	// if index is longer than the list it will append the end of it
	void* new_data = malloc(list-&gt;t_size);
	memcpy(new_data,data,list-&gt;t_size);
	ListElement* e = malloc(sizeof(struct ListElement));
	e-&gt;next = NULL;
	e-&gt;data=new_data;
	if(list-&gt;head == NULL){
		list-&gt;head=e;
		return;
	}
	if (position==0){
		e-&gt;next=list-&gt;head;
		list-&gt;head=e;
		return;
	}
	ListElement* next = list-&gt;head;
	unsigned i = 0;
	while (i+1&lt;position&amp;&amp;next-&gt;next!=NULL){
		next=next-&gt;next;
		i++;
	}
	e-&gt;next=next-&gt;next;
	next-&gt;next=e;
}

