#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;

void print_int_list(List* list){
	ListElement* next = list-&gt;head;
	while (next != NULL) {
		printf(&quot;%d&quot;, *(int*)next-&gt;data);
		next=next-&gt;next;
